SELECTCODE INV_CODE, NAME INV_NAME, ID INV_ID, RISK RISK_SCORE, COST PLANNED_COSTFROM(SELECTINVI.CODE, INVI.NAME, INVI.ID, NVL((san_pri_dev_risks*2),0)+NVL((san_pri_src_risks*2),0)+NVL((san_pri_reg_prc_risk*2),0)+NVL((san_pri_medic_risk*2),0)+NVL((san_pri_com_log_rsk*2),0) RISK, NVL(FPLAN.TOTAL_COST,0) COSTFROM INV_INVESTMENTS INVIJOIN ODF_CA_INV OCINV ON INVI.ID = OCINV.IDJOIN INV_PROJECTS INVP ON INVI.ID = INVP.PRIDLEFT JOIN CMN_LOOKUPS_V PHASE ON OCINV.san_proj_phase = PHASE.LOOKUP_CODE   AND PHASE.LOOKUP_TYPE='SAN_LKP_PROJ_PHASE'   AND PHASE.LANGUAGE_CODE = 'en'LEFT JOIN SRM_RESOURCES MAN ON MAN.USER_ID = INVI.MANAGER_IDLEFT JOIN CMN_LOOKUPS_V BU ON BU.LOOKUP_CODE = OCINV.SAN_BU   AND BU.LOOKUP_TYPE = 'SAN_LKP_BU'   AND BU.LANGUAGE_CODE = 'en'LEFT JOIN CMN_LOOKUPS_V STAGE ON STAGE.LOOKUP_CODE = OCINV.SAN_STAGE_GATE	AND STAGE.LOOKUP_TYPE = 'SAN_LKP_STAGE_GATE'	AND STAGE.LANGUAGE_CODE = 'en'LEFT JOIN FIN_PLANS FPLAN ON FPLAN.OBJECT_ID = INVI.ID AND FPLAN.PLAN_TYPE_CODE = 'FORECAST' AND FPLAN.IS_PLAN_OF_RECORD = 1WHERE INVI.IS_ACTIVE = 1AND INVP.IS_TEMPLATE = 0ORDER BY RISK DESC) RISKWHERE ROWNUM <=10AND RISK >0--AND RISK >= --AND RISK <=--AND COST <=--AND COST >=